package mixr.isabelle.pure.lib

import org.junit.Assert._
import org.junit.Test
import isabelle.Term._
import TermYXMLTest._
import java.io.{LineNumberReader, InputStreamReader}
import java.nio.charset.Charset

class TermYXMLTest {

  @Test
  def escaping_and_unescaping_a_yxml_string_should_produce_the_same_string(): Unit = {
    for (unescapedYXML <- UNESCAPED_YXMLS) {
      val escapedYXML: String = TermYXML.escapeControlChars(unescapedYXML)
      assertNotEquals(unescapedYXML, escapedYXML)
      val againUnescapedYXML: String = TermYXML.unescapeControlChars(escapedYXML)
      assertEquals(unescapedYXML, againUnescapedYXML)
    }
  }

  @Test
  def parseYXML_should_produce_the_encoded_term(): Unit = {
    val parsedTerm = TermYXML.parseYXML(SAMPLE_YXML)
    assertEquals(SAMPLE_TERM, parsedTerm)
  }

  @Test
  def outputYXML_should_produce_the_original_term_yxml(): Unit = {
    val outputYXML = TermYXML.outputYXML(SAMPLE_TERM)
    assertEquals(SAMPLE_YXML, outputYXML)
  }
}

